import React, { Component } from 'react';
import DiyList from './DiyList';
import DiyCategory from './DiyCategory';
import custom_image from '../../image/custom.png'

import {
    Container,
    Col,
    Row,
    } from 'reactstrap'

class Diy extends Component{
    id = 2
    state = {
        category: '',
        information: [
            {
                id: 0,
                name: '부품1',
                contents: '부1품부1품부1품'
            },
            {
                id: 1,
                name: '부품2',
                contents: '부2품부2품부2품'
            }
        ]
    }
    categoryInput = (data) => {
        console.log(data);
        this.setState({
            category: data
        })
    }
    render(){
        return(
            <Container>
                <br/>
                <Row>
                    <Col align="center">
                        <img src={custom_image} alt="home_image" width="40%"/>
                    </Col>
                </Row>
                <br/>
                <Row>
                    <Col>
                        <DiyCategory
                            onInput={this.categoryInput}
                        />
                    </Col>
                    <Col>
                        <DiyList
                            category={this.state.category}
                            data = {this.state.information}
                        />
                    </Col>
                </Row>
            </Container>

        );
    }
}

export default Diy;
