import React, { Component } from 'react';
import { Tab } from 'semantic-ui-react';
import axois from 'axios';
import DiyInfo from '../Diy/Diyinfo';
import { Pagination, Icon } from 'semantic-ui-react';
const panes = [
    { menuItem: 'Motor', render: () => <Tab.Pane>Motor</Tab.Pane> },
    { menuItem: 'Wings', render: () => <Tab.Pane>Wings</Tab.Pane> },
    { menuItem: 'Frame', render: () => <Tab.Pane>Frame</Tab.Pane> },
    { menuItem: 'Control Board', render: () => <Tab.Pane>Control Board</Tab.Pane> },
    { menuItem: 'ESC', render: () => <Tab.Pane>ESC</Tab.Pane> },
    { menuItem: 'Battery', render: () => <Tab.Pane>Battery</Tab.Pane> },
    { menuItem: 'Antenna', render: () => <Tab.Pane>Antenna</Tab.Pane> },
];

class Buy extends Component{
    constructor() {
        super();
        this.state = {
            pageCount: 5,
            activePage: 1
        };
    }
    handlePaginationChange = (e, { activePage }) => this.setState({ activePage })

    render(){
        const { category,
            selectParts,
            info,
            show,
        } = this.props;
        let frontIndex= this.state.pageCount*(this.state.activePage-1);
        let backIndex = this.state.pageCount*this.state.activePage;
        const list = info.filter(info => info.part === category).filter((info, index) => (index >= frontIndex && index < backIndex)).map(
            info => (<DiyInfo
        key={info._id}
        info={info}
        selectParts={selectParts}
        show = {show}
        />)
    );
        return(
            <div>
                <Tab panes={panes} />
            </div>
        );
    }
}

export default Buy;
