import React, { Component } from 'react';
import '../../style/DiyCategoryStyle.css';

class DiyCategory extends Component{
    state = {
        category: ''
    }
    categoryClick = (e) => {
        this.setState({
            category: e.target.value
        });
        this.props.onInput(this.state.category);
    }
    render(){
        return(
            <div className="category">
                <h2>Category</h2>
                <div className="category-list">
                    <button value='1' onClick={this.categoryClick}>Motor</button>
                    <button value='2' onClick={this.categoryClick}>Wings</button>
                    <button value='3' onClick={this.categoryClick}>Frame</button>
                    <button value='4' onClick={this.categoryClick}>Control Board</button>
                    <button value='5' onClick={this.categoryClick}>ESC</button>
                    <button value='6' onClick={this.categoryClick}>Battery</button>
                    <button value='7' onClick={this.categoryClick}>Antenna</button>
                </div>
            </div>

        );
    }
}

export default DiyCategory;
